class Solution:
    def __init__(self):
        self.row = 0
        self.col = 0
        self.dic = {}
        self.i = -1
        self.j = -1

    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if len(word) == 1:
            for i in board:
                for k in i:
                    if k == word[0]:
                        return True
        self.row = len(board)
        self.col = len(board[0])
        for i in range(self.row):
            for j in range(self.col):
                if board[i][j] == word[0]:
                    self.i = i
                    self.j = j
                    if self.eorn(board, [i, j], word, 0, 0):
                        return True
        return False

    def eorn(self, board, location, word, i, dir):
        self.dic[str(location[0]) + str(location[1])] = 1
        print(location)
        print(i)
        if i + 1 < len(word):
            dirs, s = self.dir(dir, location)
            for lo in s:
                if board[dirs[lo][0]][dirs[lo][1]] == word[i + 1] and i + 1 == len(word) - 1:
                    return True
                if board[dirs[lo][0]][dirs[lo][1]] == word[i + 1]:
                    self.eorn(board, dirs[lo], word, i + 1, -lo)
        else:
            return False
        self.dic.pop(str(location[0]) + str(location[1]))
        i -= 1
        #print(i, '\n')
        return False

    def dir(self, di, index):
        fi = []
        dir = {-1: [index[0] - 1, index[1]],
               -2: [index[0], index[1] - 1],
               1: [index[0] + 1, index[1]],
               2: [index[0], index[1] + 1]}
        if index[0] + 1 >= self.row:
            dir.pop(1)
        if index[0] - 1 < 0:
            dir.pop(-1)
        if index[1] - 1 < 0:
            dir.pop(-2)
        if index[1] + 1 >= self.col:
            dir.pop(2)
        for i in dir:
            if str(dir[i][0]) + str(dir[i][1]) in self.dic:
                fi.append(i)
        for i in fi:
            dir.pop(i)
        if di in dir:
            dir.pop(di)
        s = sorted(dir, key=lambda x: abs(dir[x][0] - self.i) + abs(dir[x][1] - self.j))
        return dir, s


